mapper.insert:
INSERT INTO tbl_product(
p_code, p_name, p_price, p_desc, p_image
) VALUES ( SEQ_PRODUCT.NEXTVAL, #{p_name}, #{p_price}, #{p_desc,jdbcType=VARCHAR}, #{p_image,jdbcType=VARCHAR} )

INSERT INTO tbl_size(s_code, s_p_code, s_size) SELECT SEQ_SIZE.NEXTVAL, sub.* from(
SELECT #{sizeVO.s_p_code,jdbcType=VARCHAR}, #{sizeVO.s_size,jdbcType=VARCHAR} FROM dual
) sub

INSERT INTO tbl_size(s_code, s_p_code, s_size) SELECT SEQ_SIZE.NEXTVAL, sub.* from(
SELECT #{sizeVO.s_p_code,jdbcType=VARCHAR}, #{sizeVO.s_size,jdbcType=VARCHAR} FROM dual
) sub

INSERT INTO tbl_color(c_code, c_s_code, c_color, c_qty) SELECT SEQ_COLOR.NEXTVAL, sub.* from(
SELECT #{color.c_s_code,jdbcType=VARCHAR}, #{color.c_color,jdbcType=VARCHAR}, #{color.c_qty,jdbcType=VARCHAR} FROM dual
) sub

mapper.update:
UPDATE tbl_product SET
	p_name = #{p_name},
	p_price = #{p_price},
	p_desc = #{p_desc,jdbcType=VARCHAR},
	p_image = #{p_image,jdbcType=VARCHAR}

WHERE p_code = #{p_code}
